import React, { useState} from 'react';
import Card from '@mui/material/Card';
import CardHeader from '@mui/material/CardHeader';
import CardMedia from '@mui/material/CardMedia';
import CardContent from '@mui/material/CardContent';
import Typography from '@mui/material/Typography';
const countries = [];
async function getCountries() {
  try {
    const response = await fetch('http://localhost:5000/workHuman/countries'); // Add 'http://' before 'localhost'
    const data = await response.json(); // Use response.json() to parse JSON
    countries.push(...data);
    // console.log(data);
  } catch (e) {
    console.error(e);
  }
}

// async function print() {
//   await getCountries();
//   countries.forEach((country) => {
//     console.log(country.name);
//   });
// }


const Search = () => {
  
  const [searchInput, setSearchInput] = useState('');
  const [filteredCountries, setFilteredCountries] = useState([]);
  // const countries = 
  // [
  //   { name: "Belgium", continent: "Europe" },
  //   { name: "India", continent: "Asia" },
  //   { name: "Bolivia", continent: "South America" },
  //   { name: "Ghana", continent: "Africa" },
  //   { name: "Japan", continent: "Asia" },
  //   { name: "Canada", continent: "North America" },
  //   { name: "New Zealand", continent: "Australasia" },
  //   { name: "Italy", continent: "Europe" },
  //   { name: "South Africa", continent: "Africa" },
  //   { name: "China", continent: "Asia" },
  //   { name: "Paraguay", continent: "South America" },
  //   { name: "Usa", continent: "North America" },
  //   { name: "France", continent: "Europe" },
  //   { name: "Botswana", continent: "Africa" },
  //   { name: "Spain", continent: "Europe" },
  //   { name: "Senegal", continent: "Africa" },
  //   { name: "Brazil", continent: "South America" },
  //   { name: "Denmark", continent: "Europe" },
  //   { name: "Mexico", continent: "South America" },
  //   { name: "Australia", continent: "Australasia" },
  //   { name: "Tanzania", continent: "Africa" },
  //   { name: "Bangladesh", continent: "Asia" },
  //   { name: "Portugal", continent: "Europe" },
  //   { name: "Pakistan", continent:"Asia" },
  // ];

  const handleChange = (e) => {
    const inputValue = e.target.value;
    setSearchInput(inputValue);

    // Filter countries based on the search input
    const filtered = countries.filter((country) =>
      country.name.toLowerCase().includes(inputValue.toLowerCase())
    );
    setFilteredCountries(filtered);
  };
  return (
    <div>
      <input
        type="search"
        placeholder="Search here"
        onChange={handleChange}
        value={searchInput}
      />
      <table>
        <tbody>
          {filteredCountries.map((country, index) => (
           index % 3 === 0 && ( // create new row after every 3 countries
            <tr key={index}>
              {filteredCountries.slice(index, index + 3).map((country, subIndex) => ( // slice array to get 3 countries at a time
                <td key={subIndex}>
              <Card 
                variant="outlined" 
                size={"lg"}
                sx={{ maxWidth: 600, 
                backgroundColor: "#010416", 
                borderColor: "white",
                padding: "25px",
                border: "2px solid white",
                text: "white"
              }} 
              >
                <CardHeader
                  sx = {{color: "white"}}
                  title={country.name}
                  
                />
                <CardMedia
                  component="img"
                  height="200"
                  image={country.flag}
                  alt="country flag"
                />
                <CardContent>
                  <Typography variant="body1" color="white" >
                    {"Capital: " + country.details.capital}
                  </Typography>
                  <Typography variant="body1" color="white">
                    {"Population: " + country.details.population + " people"}
                  </Typography>
                  <Typography variant="body1" color="white">
                    {"Currency: " + country.details.currency}
                  </Typography>
              </CardContent>
              </Card>
              </td>
              ))}
            </tr>
              )
          ))} 
        </tbody>
      </table>
    </div>
  );
};
getCountries();
// print();
export default Search;
